import React, { useEffect, useState } from 'react';

function App() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState('');

  const loadTasks = async () => {
    const res = await fetch('http://localhost:15000/tasks');
    const json = await res.json();
    setTasks(json);
  };

  useEffect(() => {
    loadTasks();
  }, []);

  const addTask = async () => {
    if (!text.trim()) return;
    await fetch('http://localhost:15000/tasks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ text, completed: false }),
    });
    setText('');
    loadTasks();
  };

  const toggleTask = async (task) => {
    await fetch(`http://localhost:15000/tasks/${task._id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...task, completed: !task.completed }),
    });
    loadTasks();
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:15000/tasks/${id}`, { method: 'DELETE' });
    loadTasks();
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>To-Do App</h1>

      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Add new task"
      />
      <button onClick={addTask}>Add</button>

      <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
        {tasks.map((task) => (
          <li key={task._id} style={{ marginTop: 8 }}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleTask(task)}
            />
            <span
              style={{
                marginLeft: 8,
                textDecoration: task.completed ? 'line-through' : 'none',
              }}
            >
              {task.text}
            </span>
            <button
              onClick={() => deleteTask(task._id)}
              style={{ marginLeft: 10 }}
            >
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
